sql.where:
AND headline_examination_id = examination_id

	AND headline_examination_id = #{headlineExaminationId,jdbcType=VARCHAR}


	AND headline_headline_subject like #{headlineHeadlineSubject,jdbcType=VARCHAR}


	AND headline_examination_content like #{headlineExaminationContent,jdbcType=VARCHAR}


	AND headline_question_count = #{headlineQuestionCount,jdbcType=INTEGER}


	AND headline_score = #{headlineScore,jdbcType=INTEGER}


	AND headline_pattern = #{headlinePattern,jdbcType=INTEGER}


	AND headline_answer_pattern = #{headlineAnswerPattern,jdbcType=INTEGER}


	AND headline_sort = #{headlineSort,jdbcType=INTEGER}


	AND headline_question_type = #{headlineQuestionType,jdbcType=VARCHAR}

mapper.select:
select ,examination_subject headline_examination_name from headline,examination
order by ${sortField} ${sortOrder}
order by headline_examination_id,headline_sort asc
