mapper.update:
update user_question
set 
user_question_user_answer = #{userQuestionUserAnswer,jdbcType=VARCHAR}
where user_question_examination_id = #{userQuestionExaminationId,jdbcType=VARCHAR}
AND user_question_userid = #{userQuestionUserid,jdbcType=VARCHAR}
AND user_question_question_id = #{userQuestionQuestionId,jdbcType=VARCHAR}

update.foreach:
update user_question
set 
user_question_user_answer = #{item.userQuestionUserAnswer,jdbcType=VARCHAR}
where user_question_examination_id = #{item.userQuestionExaminationId,jdbcType=VARCHAR}
AND user_question_userid = #{item.userQuestionUserid,jdbcType=VARCHAR}
AND user_question_question_id = #{item.userQuestionQuestionId,jdbcType=VARCHAR}

mapper.select:
SELECT Sum(user_question.user_question_score)
FROM user_question
WHERE user_question.user_question_right_answer = user_question.user_question_user_answer
	AND user_question_examination_id = #{userQuestionExaminationId,jdbcType=VARCHAR}
	AND user_question_userid = #{userQuestionUserid,jdbcType=VARCHAR}
